const phoneRegex = /^\+?[0-9]+$/;
confirmBtn.addEventListener('click', () => {

    if (hashInput.value.length < 63) {
        hashInput.classList.add('placeholder:text-red-500');
        iziToast.error({
            pauseOnHover: true,
            title: 'Error',
            transitionIn: 'bounceInLeft',
            message: 'Please enter correct transaction hash',
            position: 'topRight',
            color: '#f87171'

        });

    }
    else if (nameInput.value.length < 3) {

        nameInput.classList.add('placeholder:text-red-500');
        iziToast.error({
            pauseOnHover: true,
            title: 'Error',
            transitionIn: 'bounceInLeft',
            message: 'Please enter your full name',
            position: 'topRight',
            color: '#f87171'

        });
    } else if (phoneInput.value.length < 6) {

        phoneInput.classList.add('placeholder:text-red-500');
        iziToast.error({
            pauseOnHover: true,
            title: 'Error',
            transitionIn: 'bounceInLeft',
            message: 'Please enter whatsapp number',
            position: 'topRight',
            color: '#f87171'

        });
    } else if (!phoneRegex.test(phoneInput.value)) {

        phoneInput.classList.add('placeholder:text-red-500');
        iziToast.error({
            pauseOnHover: true,
            title: 'Error',
            transitionIn: 'bounceInLeft',
            message: 'Please enter correct number',
            position: 'topRight',
            color: '#f87171'

        });
    } else {
        phoneInput.classList.remove('placeholder:text-red-500');
        hashInput.classList.remove('placeholder:text-red-500');
        nameInput.classList.remove('placeholder:text-red-500');

        loadingElem.classList.remove('hidden')
        confirmTitle.classList.add('hidden')
        confirmBtn.style.opacity = '0.5'

        const apiKey = '292d860a-efba-484c-ba08-909fc5b11e25';
        const endpoint = `https://apilist.tronscanapi.com/api/transaction-info?hash=${hashInput.value}`;

        fetch(endpoint, {
            headers: {
                'TRON-PRO-API-KEY': apiKey

            }
        })
            .then(response => response.json())
            .then(data => {
                console.log(data)
                loadingElem.classList.add('hidden')
                confirmTitle.classList.remove('hidden')
                confirmBtn.style.opacity = '1'

                if (data.contractRet == 'SUCCESS') {
                    
                    if (data.tokenTransferInfo.to_address != 'TY3LdgPmj7dXfTnXh9VhBnxmKadYHdcPpy') {
                        iziToast.error({
                            pauseOnHover: true,
                            title: 'Error',
                            transitionIn: 'bounceInLeft',
                            message: 'Your transiction is not to our wallet address',
                            position: 'topRight',
                            color: '#f87171'

                        });
                    }

                    else if (parseInt(data.tokenTransferInfo.amount_str) != parseInt(priceElem.textContent) * 1000000) {
                        iziToast.error({
                            pauseOnHover: true,
                            title: 'Error',
                            transitionIn: 'bounceInLeft',
                            message: 'Amount is not equal to course price!, contact us',
                            position: 'topRight',
                            color: '#f87171'


                        });
                    }
                    else if(!data.tokenTransferInfo){
                        iziToast.error({
                            pauseOnHover: true,
                            title: 'Error',
                            transitionIn: 'bounceInLeft',
                            message: 'Your payment is not token is not USDT trc20',
                            position: 'topRight',
                            color: '#f87171'


                        });
                    }
                    else {
                        iziToast.success({
                            pauseOnHover: true,
                            timeout: 5000,
                            title: 'Confirmed',
                            transitionIn: 'bounceInDown',
                            message: 'we will contact you within a hour',
                            position: 'topCenter',
                            color: '#4ade80'

                        });

                        overlayElem.classList.remove('hidden')
                        modalElem.classList.remove('hidden')
                        paymentBox.classList.add('-z-50')
                    }

                }
                else if (data.contractRet == 'REVERT') {
                    iziToast.error({
                        pauseOnHover: true,
                        title: 'Error',
                        transitionIn: 'bounceInLeft',
                        message: 'You paste a failed transaction ',
                        position: 'topRight',
                        color: '#f87171'

                    });
                }
                else {
                  
                    iziToast.error({
                        pauseOnHover: true,
                        title: 'Error',
                        transitionIn: 'bounceInLeft',
                        message: 'paste a vaild transaction hash ',
                        position: 'topRight',
                        color: '#f87171'

                    });
                }
            }).catch(err => {
                iziToast.error({
                    title: 'Error',
                    transitionIn: 'bounceInLeft',
                    message: 'there is problem, contact with support',
                    position: 'topRight',
                    color: '#f87171'

                });
            })




    }


})




